import React, {useRef} from "react";


function App() {
    const rootRef = useRef(null);

    const changeColor = () => {
        let i = 1;

        if(rootRef.current.style.backgroundColor === "white"){
            rootRef.current.style.backgroundColor = "black";
        } else {
            rootRef.current.style.backgroundColor = "white";
        }

    }
    return(
    <>
    <div ref={rootRef}>
        <button onClick={changeColor}>Change color</button>
    </div>
    </>
    )
}

export default App;
