import React, {useRef} from "react";


function App() {
    const rootRef = useRef(null);

    const changeColor = () => {
        if(rootRef.current){
            rootRef.current.style.backgroundColor = "white";
        }

    const changeWhite = () => {
        if (rootRef.current) {
            rootRef.current.style.backgroundColor = "black";
        }
    }
    }
    return(
    <>
    <div ref={rootRef}>
        <button onClick={changeColor} onDoubleClick={changeWhite}>Change color</button>
    </div>
    </>
    )
}

export default App;
