import React, {useRef} from "react";
import Button from ""

function App() {
const rootREf = useRef(null);

    const changeColor = () => {
        if(rootREf.current){
            rootREf.current.style.background = "white";
        }
    }
    return(
    <>
    <button onClick={changeColor}>Change color</button>
    </>
    )
}

export default App;
